import { format, parseISO } from 'date-fns';
import { ru } from 'date-fns/locale';

const safeFormat = (fn) => {
  try {
    return fn();
  } catch (error) {
    console.error('Ошибка при форматировании:', error);
    return '';
  }
};

export const formatDate = (isoString) =>
  safeFormat(() => format(parseISO(isoString), 'dd.MM.yyyy', { locale: ru }));

export const formatTime = (timeString) =>
  safeFormat(() => format(new Date(`1970-01-01T${timeString}:00`), 'HH:mm'));

export const formatDateTime = (isoString) =>
  safeFormat(() => format(parseISO(isoString), 'dd.MM.yyyy HH:mm', { locale: ru }));
