import React, { useState } from 'react';
import { TouchableOpacity, Text, View } from 'react-native';
import styles from '../styles/styles';

const AddButton = ({ onPress }) => {
  const [isPressed, setIsPressed] = useState(false);

  const handlePressIn = () => {
    setIsPressed(true);
  };

  const handlePressOut = () => {
    onPress(); // Вызовите функцию onPress, переданную из родительского компонента
    setIsPressed(false);
  };

  const addButtonStyles = [
    styles.addButton,
    isPressed ? styles.addButtonPressed : null,
    { position: 'absolute', bottom: 16, left: 16, right: 16 },
  ];

  const addButtonTextStyles = [
    styles.addButtonText,
    isPressed ? styles.addButtonTextPressed : null,
  ];

  return (
    <View style={addButtonStyles}>
      <TouchableOpacity
        style={{ flex: 1, justifyContent: 'center' }}
        onPressIn={handlePressIn}
        onPressOut={handlePressOut}
      >
        <Text style={addButtonTextStyles}>Добавить задачу</Text>
      </TouchableOpacity>
    </View>
  );
};

export default AddButton;
