import React, { useState, useCallback, useEffect } from 'react';
import { View, Text, TouchableOpacity, Modal } from 'react-native';
import moment from 'moment';
import 'moment/locale/ru';
import styles from '../styles/styles';
import { CalendarIcon, TodayIcon } from '../icons';
import AddButton from '../components/AddButton';
import CustomCalendar from '../components/CustomCalendar';
import VerticalCalendar from '../components/VerticalCalendar';
import NewTaskScreen from './NewTaskScreen';
import axios from 'axios';

const TasksScreen = () => {
  const [tasks, setTasks] = useState([]);
  const [isNewTaskScreenVisible, setNewTaskScreenVisible] = useState(false);

  moment.locale('ru');
  const [selectedDate, setSelectedDate] = useState(moment());
  const [viewMode, setViewMode] = useState('today');
  const [headerTitle, setHeaderTitle] = useState('Сегодня');

  const fetchTasksFromServer = () => {
    axios.get('http://localhost/tasks') // Замените на ваш URL сервера
      .then((response) => {
        setTasks(response.data); // Устанавливаем данные в state
      })
      .catch((error) => {
        console.error('Ошибка при загрузке данных с сервера:', error);
      });
  };

  // Вызываем функцию загрузки данных с сервера при монтировании компонента или при изменении выбранной даты
  useEffect(() => {
    fetchTasksFromServer();
  }, [selectedDate]);

  /*const initialTasks = [
    {
      date: '2023-10-12',
      tasks: [
        { id: 1, title: 'Выполнено', status: 'новая', client: 'Иванов Иван' },
      ],
    },
    {
      date: '2023-10-13',
      tasks: [
        { id: 2, title: 'Задача 1', status: 'новая', client: 'Петров Петр' },
        { id: 3, title: 'Задача 2', status: 'выполняется', client: 'Петров Петр' },
        { id: 4, title: 'Выполнено', status: 'новая', client: 'Иванов Иван' },
      ],
    },
  ];
  

  const [tasks, setTasks] = useState(initialTasks);*/

  const toggleVerticalCalendar = useCallback(() => {
    setViewMode(viewMode === 'today' ? 'calendar' : 'today');
  }, [viewMode]);

  const updateHeaderTitle = useCallback((date) => {
    setHeaderTitle(date.isSame(moment(), 'day') ? 'Сегодня' : date.format('D MMMM'));
  }, []);

  return (
    <View style={styles.container}>
      <View style={styles.contentContainer}>
        <View style={styles.taskHeader}>
          <Text style={styles.titleMedium}>
            {viewMode === 'today' ? headerTitle : 'Календарь'}
          </Text>
          <View style={styles.calendarBlock}>
            <Text style={styles.title}>{selectedDate.format('MMMM, YYYY')}</Text>
            <TouchableOpacity onPress={toggleVerticalCalendar}>
              {viewMode === 'today' ? (
                <CalendarIcon />
              ) : (
                <TodayIcon />
              )}
            </TouchableOpacity>
          </View>
        </View>
        {viewMode === 'today' ? (
          <CustomCalendar
            selectedDate={selectedDate}
            onDateChange={(date) => {
              setSelectedDate(date);
              updateHeaderTitle(date);
              fetchTasksFromServer();
            }}
            tasks={tasks}
          />
        ) : (
          <VerticalCalendar
            selectedDate={selectedDate}
            onDateChange={(date) => {
              setSelectedDate(date);
              updateHeaderTitle(date);
              fetchTasksFromServer();
            }}
            tasks={tasks}
            viewMode={viewMode} 
          />
        )}
        <AddButton onPress={() => setNewTaskScreenVisible(true)} />
        <Modal
          visible={isNewTaskScreenVisible}
          animationType="slide"
          onRequestClose={() => setNewTaskScreenVisible(false)}
        >
          <NewTaskScreen onClose={() => setNewTaskScreenVisible(false)} />
        </Modal>
      </View>
    </View>
  );
};

export default TasksScreen;
