import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, ScrollView } from 'react-native';
import axios from 'axios';
import DropdownWithSearch from '../components/DropdownWithSearch';
import Dropdown from '../components/Dropdown';
import DropdownClient from '../components/DropdownClient';
import moment from 'moment';
import DateTimePickerModal from 'react-native-modal-datetime-picker';
import { BackIcon, DeleteIcon, CalendarIcon } from '../icons';
import styles from '../styles/styles';

function NewTaskScreen({ onSave, onClose }) {
  const statusOptions = ['Новая', 'В процессе', 'Выполнено', 'Отменено'];
  const assignedOptions = ['Ответственный 1', 'Ответственный 2'];
  const relatedClientOptions = ['Клиент 1', 'Клиент 2', 'Клиент 3', 'Клиент 4', 'Клиент 5', 'Клиент 6', 'Клиент 7', 'Фред'];
  const userOptions = ['Участник 1', 'Участник 2', 'Участник 3'];
  const [status, setStatus] = useState('Новая');
  const [startDate, setStartDate] = useState('');
  const [assignedId, setAssignedId] = useState('Ответственный 1');
  const [relatedClient, setRelatedClient] = useState('Клиент 1');
  const [addressClient, setAddressClient] = useState('Где-то там, где-то тут, где-то здесь, 10к1');
  const [users, setUsers] = useState('1');
  const [paymentMethod, setPaymentMethod] = useState('Наличные'); // Изначальный способ оплаты
  const [cost, setCost] = useState('0'); // Изначальная стоимость

  const [description, setDescription] = useState('');
  const [endDate, setEndDate] = useState('');
  const [maxDate, setMaxDate] = useState(new Date());
  const [isStartPickerVisible, setStartPickerVisible] = useState(false);
  const [isEndPickerVisible, setEndPickerVisible] = useState(false);
  const [startDateTime, setStartDateTime] = useState(null);
  const [endDateTime, setEndDateTime] = useState(null);
  const paymentMethodOptions = ['Наличные', 'Кредитная карта', 'Перевод', 'Другое'];
  const costOptions = ['0', '10', '25', '50', '100'];


  // Function to show/hide the TimePicker
  const toggleStartPicker = () => {
    setStartPickerVisible(!isStartPickerVisible);
  };

  const toggleEndPicker = () => {
    setEndPickerVisible(!isEndPickerVisible);
  };

  const handleStartPicked = (date) => {
    setStartDateTime(date);
    toggleStartPicker();
  };

  const handleEndPicked = (date) => {
    setEndDateTime(date);
    toggleEndPicker();
  };

  const handleStartDateChange = (date) => {
    if (date <= new Date()) {
      setStartDate(new Date());
      if (endDate <= new Date() || endDate <= date) {
        setEndDate(new Date());
      }
    } else {
      setStartDate(date);
      if (endDate <= date) {
        setEndDate(date);
      }
    }
  };
  
  const handleEndDateChange = (date) => {
    if (date <= startDate || date <= new Date()) {
      setEndDate(startDate);
    } else {
      setEndDate(date);
    }
  };
  

  const desiredMaxDate = new Date();
  desiredMaxDate.setDate(maxDate.getDate() + 365);

  const handleSave = () => {
    const taskData = {
      status,
      start_date: startDate.toISOString().split('T')[0],
      deadline: endDate.toISOString().split('T')[0],
      assigned_id: assignedId,
      related_client: relatedClient,
      address_client: addressClient,
      users,
      description,
      payment_method: paymentMethod,
      cost,
    };

    axios
      .post('http://31.129.101.174/add-task', taskData)
      .then((response) => {
        console.log('Задача успешно добавлена', response.data);
      })
      .catch((error) => {
        console.error('Ошибка при добавлении задачи:', error);
      });
  };

  function DateInput({ date, placeholder, onDateChange, dateType, minDate, maxDate }) {
    const [isDatePickerVisible, setDatePickerVisibility] = useState(false);
  
    const showDatePicker = () => {
      setDatePickerVisibility(true);
    };
  
    const hideDatePicker = () => {
      setDatePickerVisibility(false);
    };
  
    const handleDateConfirm = (selectedDate) => {
      hideDatePicker();
      onDateChange(selectedDate);
    };
  
    return (
      <View>
        <TouchableOpacity onPress={showDatePicker}>
          <View style={styles.dateInputContainer}>
            <TextInput
              value={date ? date.toISOString().split('T')[0] : ''}
              placeholder={placeholder}
              editable={false}
              style={styles.dateInputText}
            />
            <TouchableOpacity style={styles.dateInputButton} onPress={showDatePicker}>
              <CalendarIcon />
            </TouchableOpacity>
          </View>
        </TouchableOpacity>
        <DateTimePickerModal
          isVisible={isDatePickerVisible}
          mode="date"
          onConfirm={handleDateConfirm}
          onCancel={hideDatePicker}
          minimumDate={minDate}
          maximumDate={maxDate}
          date={date || new Date()}
        />
      </View>
    );
  }
  
  return (
    <ScrollView style={[styles.contentContainerTask]}>
      <View style={styles.taskHeader}>
        <BackIcon />
        <Text style={[styles.titleMedium, { flex: 1, textAlign: 'center' }]}>Добавить задачу</Text>
        <DeleteIcon />
      </View>
      <Dropdown label="Статус" options={statusOptions} selectedValue={status} onValueChange={setStatus} />
      
      <View style={{ flexDirection: 'row'}}>
          <Dropdown label="Способ оплаты" options={paymentMethodOptions} selectedValue={paymentMethod} onValueChange={setPaymentMethod} />
        <View style={{ flex: 1, marginLeft: 8 }}>
          <Text style={styles.label}>Стоимость</Text>
          <TextInput
            style={styles.costInput}
            placeholder="Стоимость"
            value={cost}
            onChangeText={setCost}
            keyboardType="numeric"
          />
        </View>
      </View>


      <View style={{ flexDirection: 'row', marginVertical: 24 }}>
        <View style={{ flex: 1, marginRight: 8 }}>
          <Text style={styles.dateInputLabel}>Начальная дата</Text>
          <DateInput
            date={startDate}
            placeholder="ГГГГ-ММ-ДД"
            onDateChange={handleStartDateChange}
            minDate={new Date()}
            maxDate={desiredMaxDate}
          />
        </View>

        <View style={{ flex: 1 }}>
          <Text style={styles.dateInputLabel}>Конечная дата</Text>
          <DateInput
            date={endDate}
            placeholder="ГГГГ-ММ-ДД"
            onDateChange={handleEndDateChange}
            minDate={startDate || new Date()}
            maxDate={desiredMaxDate}
          />
        </View>
      </View>
      
      <View style={{ flexDirection: 'row', alignItems: 'center', marginBottom: 24}}>
        <TouchableOpacity onPress={toggleStartPicker}>
          <View style={{ flex: 1 }}>
            <TextInput
              value={startDateTime ? moment(startDateTime).format('HH:mm') : ''}
              placeholder="HH:mm"
              editable={false}
              style={[styles.dateInputText, styles.timeInputStyle]}
            />
          </View>
        </TouchableOpacity>

        <View style={{ width: 16 }} /> 
        
        <TouchableOpacity onPress={toggleEndPicker}>
          <View style={{ flex: 1 }}>
            <TextInput
              value={endDateTime ? moment(endDateTime).format('HH:mm') : ''}
              placeholder="HH:mm"
              editable={false}
              style={[styles.dateInputText, styles.timeInputStyle]}
            />
          </View>
        </TouchableOpacity>
      </View>

      <DateTimePickerModal
        isVisible={isStartPickerVisible}
        mode="time"
        onConfirm={handleStartPicked}
        onCancel={toggleStartPicker}
        is24Hour={true}
        date={startDateTime || new Date()}
        style={styles.timePickerStyle}
      />

      <DateTimePickerModal
        isVisible={isEndPickerVisible}
        mode="time"
        onConfirm={handleEndPicked}
        onCancel={toggleEndPicker}
        is24Hour={true}
        date={endDateTime || new Date()}
        style={styles.timePickerStyle}
      />

      <DropdownWithSearch label="Ответственный" options={assignedOptions} selectedValue={assignedId} onValueChange={setAssignedId} />
      <DropdownWithSearch label="Участники" options={userOptions} selectedValue={users} onChangeValue={setUsers} />
      <DropdownClient label="Связанный клиент" options={relatedClientOptions} selectedValue={relatedClient} onValueChange={setRelatedClient} />
      
      <View style={styles.commentContainer}>
        <Text style={styles.label}>Комментарий</Text>
          <View>
            <TextInput
              placeholder="Комментарий"
              value={description}
              onChangeText={setDescription}
              multiline={true}
              numberOfLines={4}
              style={styles.commentInput}
            />
          </View>
      </View>
      
      <TouchableOpacity style={styles.button} onPress={handleSave}>
        <Text style={styles.buttonText}>Сохранить</Text>
      </TouchableOpacity>
    </ScrollView>
  );
}

export default NewTaskScreen;
